astebin.js"></script>-->    <script type="text/javascript" src="../bower_components/bootstrap-filestyle/src/bootstrap-filestyle.min.js"></script>    <script>        $(document).ready(function () {            $('.sidebar-menu').tree();            $( "#mainForm" ).validate( {                rules: {                    nome: {                        required: true                    },                    email: {                        required: true,                        email: true                    },                    senha: {                        required: {                            depends: function (element) {                                return $("#resenha").val().length >= 0;                            },                        },                        minlength: 5                    },                    resenha: {                        required: {                            depends: function (element) {                                return $("#senha").val().length >= 0;                            }                        },                        minlength: 5,                        equalTo: "#senha"                    },                    tipo: {                        required: true                    }                },                messages: {                    nome: {                        required: "Por favor, inform o nome do usu&aacute;rio"                    },                    email: {                        required: "Por favor, informe um e-mail",                        email: "Por favor, informe um e-mail válido"                    },                    senha: {                        required: "Por favor, informe uma senha",                        minlength: "Sua senha precisa ter ao menos 5 caracteres"                    },                    resenha: {                        required: "Por favor, informe uma senha",                        minlength: "Sua senha precisa ter 